!function(){tinymce.PluginManager.add("symple_shortcodes_mce_button",function(t,n){t.addButton("symple_shortcodes_mce_button",{title:"Symple Shortcodes",type:"menubutton",icon:"icon symple-shortcodes-icon",menu:[{text:"Layout",menu:[{text:"Column",onclick:function(){t.insertContent('[symple_column size="one-half" position="first"]<br />Content<br />[/symple_column]')}},{text:"Divider",onclick:function(){t.insertContent("[divider]")}}]},{text:"Elements",menu:[{text:"Button",onclick:function(){t.insertContent('[button url="http://www.url.com" class="btn-primary" target="self"]Button Text[/button]')}},{text:"Special Link",onclick:function(){t.insertContent('[special_link text="Link Text" url="#"]')}},{text:"Small",onclick:function(){t.insertContent("[small]Text Here[/small]")}},{text:"Accent Font",onclick:function(){t.insertContent("[accent_font]Text[/accent_font]")}},{text:"Lead Text",onclick:function(){t.insertContent("[lead]Text[/lead]")}},{text:"Special List",onclick:function(){t.insertContent("[special_list]<br />Add list here<br />[/special_list]")}},{text:"Testimonial",onclick:function(){t.insertContent('[testimonial by="Author Name"]Content[/testimonial]')}}]},{text:"jQuery",menu:[{text:"Toggle",onclick:function(){t.insertContent('[toggle title="Toggle Title"]Content[/toggle]')}}]}]})})}();